import React, { useEffect, useState } from 'react';
import { View, Text, FlatList, Button, Image, StyleSheet } from 'react-native';

const CarList = ({ navigation }) => {
  const [cars, setCars] = useState([]);

  useEffect(() => {
    // Implement logic to fetch the list of cars
    fetch('http://localhost:8080/car')
      .then(response => response.json())
      .then(data => setCars(data))
      .catch(error => console.error('Error fetching cars', error));
  }, []);

  const navigateToCarDetail = (car) => {
    // Navigate to the CarDetail screen and pass the car data
    navigation.navigate('CarDetail', { car });
  };

  const navigateToCarForm = () => {
    // Navigate to the CarForm screen
    navigation.navigate('CarForm');
  };

  return (
    <View>
      <FlatList
        data={cars}
        keyExtractor={(item) => item.id.toString()}
        renderItem={({ item }) => (
          <View style={styles.carContainer}>
            <Image source={{ uri: item.image }} style={styles.carImage} />
            <Text>{item.title}</Text>
            <Button title="Details" onPress={() => navigateToCarDetail(item)} />
          </View>
        )}
      />
      <Button title="Add Car" onPress={navigateToCarForm} />
    </View>
  );
};

const styles = StyleSheet.create({
  carContainer: {
    marginBottom: 20,
    alignItems: 'center',
  },
  carImage: {
    width: 200,
    height: 100,
    marginBottom: 10,
  },
});

export default CarList;
